// Shared Components
function ActionButtons<T extends { record_id: string }>({ item, onEdit, onDelete }: {
  item: T;
  onEdit: (item: T) => void;
  onDelete: (id: string) => void;
}) {
  return (
    <div className="flex gap-1">
      <Button variant="ghost" size="icon" onClick={() => onEdit(item)}>
        <Pencil className="h-4 w-4" />
