        >
          <SelectTrigger>
            <SelectValue placeholder="Dozent ausw√§hlen" />
          </SelectTrigger>
          <SelectContent>
            {dozenten.map(d => (
              <SelectItem key={d.record_id} value={d.record_id}>{d.fields.name}</SelectItem>
            ))}
          </SelectContent>
        </Select>
      </div>

      <div className="space-y-2">
        <Label htmlFor="raum">Raum</Label>
        <Select
          value={extractRecordId(formData.raum) || ''}
          onValueChange={v => setFormData({...formData, raum: v ? createRecordUrl(APP_IDS.RAEUME, v) : undefined})}
        >
          <SelectTrigger>
            <SelectValue placeholder="Raum ausw√§hlen" />
          </SelectTrigger>
          <SelectContent>
            {raeume.map(r => (
              <SelectItem key={r.record_id} value={r.record_id}>
                {r.fields.raumname} {r.fields.gebaeude ? `(${r.fields.gebaeude})` : ''}
              </SelectItem>
            ))}
          </SelectContent>
        </Select>
      </div>

      <DialogFooter>
        <DialogClose asChild>
          <Button type="button" variant="outline">Abbrechen</Button>
        </DialogClose>
        <Button type="submit" disabled={saving}>
          {saving ? <Spinner className="h-4 w-4 mr-2" /> : null}
          {editingItem ? 'Speichern' : 'Erstellen'}
        </Button>
      </DialogFooter>
    </form>
  );
}

